// Generate 500 fake test payloads
const items = [];
for (let i = 1; i <= 500; i++) {
  items.push({
    json: {
      id: `test-${i}`,
      priority: i % 2 === 0 ? "high" : "medium"
    }
  });
}
return items;

// Simulate execution outcome for each item
return items.map(item => {
  const rand = Math.random();
  const status = rand > 0.1 ? 'success' : 'error';  // 90% success rate
  return {
    json: {
      ...item.json,
      status
    }
  };
});

// Count how many succeeded vs failed
let success = 0;
let error = 0;

for (const item of items) {
  if (item.json.status === "success") success++;
  else error++;
}

return [{
  json: {
    total: 500,
    success,
    error,
    successRate: (success / 500 * 100).toFixed(2) + "%",
    errorRate: (error / 500 * 100).toFixed(2) + "%"
  }
}];
